                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            collider = @params.collider;
            sender = @params.sender;
            orig(self, collider, sender);
            if (_afterHandleTriggerEnter != null)
            {
                foreach (Delegates.HandleTriggerEnter_AfterArgs toInvoke in
                         _afterHandleTriggerEnter.GetInvocationList())
                {
                    try
                    {
                        _afterHandleTriggerEnter.Invoke(@params);
