    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Awake
        {
            public StalactiteControl self;
        }

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<StalactiteControl> orig, StalactiteControl self);

        public sealed class Params_Start
        {
            public StalactiteControl self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<StalactiteControl> orig, StalactiteControl self);

        public sealed class Params_HandleTriggerEnter
        {
            public StalactiteControl self;
            public UnityEngine.Collider2D collider;
            public UnityEngine.GameObject sender;
        }

        public delegate void HandleTriggerEnter_BeforeArgs(Params_HandleTriggerEnter args);

        public delegate void HandleTriggerEnter_AfterArgs(Params_HandleTriggerEnter args);

        public delegate void HandleTriggerEnter_WithArgs(
            Action<StalactiteControl, UnityEngine.Collider2D, UnityEngine.GameObject> orig, StalactiteControl self,
            UnityEngine.Collider2D collider, UnityEngine.GameObject sender);

        public sealed class Params_Fall
        {
            public StalactiteControl self;
            public float delay;
        }

        public delegate void Fall_BeforeArgs(Params_Fall args);

        public delegate System.Collections.IEnumerator Fall_AfterArgs(Params_Fall args,
            System.Collections.IEnumerator ret);

        public delegate System.Collections.IEnumerator Fall_WithArgs(
            Func<StalactiteControl, float, System.Collections.IEnumerator> orig, StalactiteControl self, float delay);

        public sealed class Params_OnTriggerEnter2D
        {
            public StalactiteControl self;
            public UnityEngine.Collider2D collision;
        }

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<StalactiteControl, UnityEngine.Collider2D> orig,
            StalactiteControl self, UnityEngine.Collider2D collision);

        public sealed class Params_FlingObjects
        {
            public StalactiteControl self;
        }

        public delegate void FlingObjects_BeforeArgs(Params_FlingObjects args);

        public delegate void FlingObjects_AfterArgs(Params_FlingObjects args);

        public delegate void FlingObjects_WithArgs(Action<StalactiteControl> orig, StalactiteControl self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.HandleTriggerEnter_BeforeArgs HandleTriggerEnter
        {
            add
            {
                HookHandler._beforeHandleTriggerEnter += value;
                HookHandler.HookHandleTriggerEnter();
            }
            remove => HookHandler._beforeHandleTriggerEnter -= value;
        }

        public static event Delegates.Fall_BeforeArgs Fall
        {
            add
            {
                HookHandler._beforeFall += value;
                HookHandler.HookFall();
            }
            remove => HookHandler._beforeFall -= value;
        }

        public static event Delegates.OnTriggerEnter2D_BeforeArgs OnTriggerEnter2D
        {
            add
            {
                HookHandler._beforeOnTriggerEnter2D += value;
                HookHandler.HookOnTriggerEnter2D();
            }
            remove => HookHandler._beforeOnTriggerEnter2D -= value;
        }

        public static event Delegates.FlingObjects_BeforeArgs FlingObjects
        {
            add
            {
                HookHandler._beforeFlingObjects += value;
                HookHandler.HookFlingObjects();
            }
            remove => HookHandler._beforeFlingObjects -= value;
        }
    }

