    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Awake
        {
            public StopAnimatorsAtPoint self;
        }

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<StopAnimatorsAtPoint> orig, StopAnimatorsAtPoint self);

        public sealed class Params_Start
        {
            public StopAnimatorsAtPoint self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<StopAnimatorsAtPoint> orig, StopAnimatorsAtPoint self);

        public sealed class Params_SetCanStop
        {
            public StopAnimatorsAtPoint self;
        }

        public delegate void SetCanStop_BeforeArgs(Params_SetCanStop args);

        public delegate void SetCanStop_AfterArgs(Params_SetCanStop args);

        public delegate void SetCanStop_WithArgs(Action<StopAnimatorsAtPoint> orig, StopAnimatorsAtPoint self);

        public sealed class Params_SetCannotStop
