namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for StopAnimatorsAtPoint class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnStopAnimatorsAtPoint
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookAwake()
        {
            if (!HookedList.Contains("Awake"))
            {
                HookedList.Add("Awake");
                On.StopAnimatorsAtPoint.Awake += Awake;
            }
        }

        internal static event Delegates.Awake_BeforeArgs _beforeAwake;
        internal static event Delegates.Awake_NormalArgs _afterAwake;

        private static void Awake(On.StopAnimatorsAtPoint.orig_Awake orig, StopAnimatorsAtPoint self)
        {
            Delegates.Params_Awake @params = new()
            {
                self = self
            };
            _beforeAwake?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterAwake != null) _afterAwake.Invoke(@params);
        }

        internal static void HookStart()
        {
            if (!HookedList.Contains("Start"))
            {
                HookedList.Add("Start");
                On.StopAnimatorsAtPoint.Start += Start;
            }
        }

        internal static event Delegates.Start_BeforeArgs _beforeStart;
        internal static event Delegates.Start_NormalArgs _afterStart;

        private static void Start(On.StopAnimatorsAtPoint.orig_Start orig, StopAnimatorsAtPoint self)
        {
            Delegates.Params_Start @params = new()
            {
                self = self
            };
            _beforeStart?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterStart != null) _afterStart.Invoke(@params);
        }

        internal static void HookSetCanStop()
        {
            if (!HookedList.Contains("SetCanStop"))
            {
                HookedList.Add("SetCanStop");
                On.StopAnimatorsAtPoint.SetCanStop += SetCanStop;
            }
        }

        internal static event Delegates.SetCanStop_BeforeArgs _beforeSetCanStop;
        internal static event Delegates.SetCanStop_NormalArgs _afterSetCanStop;

        private static void SetCanStop(On.StopAnimatorsAtPoint.orig_SetCanStop orig, StopAnimatorsAtPoint self)
        {
            Delegates.Params_SetCanStop @params = new()
            {
                self = self
            };
            _beforeSetCanStop?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterSetCanStop != null) _afterSetCanStop.Invoke(@params);
        }

        internal static void HookSetCannotStop()
        {
            if (!HookedList.Contains("SetCannotStop"))
            {
                HookedList.Add("SetCannotStop");
                On.StopAnimatorsAtPoint.SetCannotStop += SetCannotStop;
            }
        }

        internal static event Delegates.SetCannotStop_BeforeArgs _beforeSetCannotStop;
        internal static event Delegates.SetCannotStop_NormalArgs _afterSetCannotStop;

        private static void SetCannotStop(On.StopAnimatorsAtPoint.orig_SetCannotStop orig, StopAnimatorsAtPoint self)
        {
            Delegates.Params_SetCannotStop @params = new()
            {
                self = self
            };
            _beforeSetCannotStop?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterSetCannotStop != null) _afterSetCannotStop.Invoke(@params);
        }

        internal static void HookUpdate()
        {
            if (!HookedList.Contains("Update"))
            {
                HookedList.Add("Update");
                On.StopAnimatorsAtPoint.Update += Update;
            }
        }

        internal static event Delegates.Update_BeforeArgs _beforeUpdate;
        internal static event Delegates.Update_NormalArgs _afterUpdate;

        private static void Update(On.StopAnimatorsAtPoint.orig_Update orig, StopAnimatorsAtPoint self)
        {
            Delegates.Params_Update @params = new()
            {
                self = self
            };
            _beforeUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterUpdate != null) _afterUpdate.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_NormalArgs(Params_Awake args);

        public delegate void SetCannotStop_BeforeArgs(Params_SetCannotStop args);

        public delegate void SetCannotStop_NormalArgs(Params_SetCannotStop args);

        public delegate void SetCanStop_BeforeArgs(Params_SetCanStop args);

        public delegate void SetCanStop_NormalArgs(Params_SetCanStop args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_NormalArgs(Params_Start args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_NormalArgs(Params_Update args);

        public sealed class Params_Awake
        {
            public StopAnimatorsAtPoint self;
        }

        public sealed class Params_Start
        {
            public StopAnimatorsAtPoint self;
        }

        public sealed class Params_SetCanStop
        {
            public StopAnimatorsAtPoint self;
        }

        public sealed class Params_SetCannotStop
        {
            public StopAnimatorsAtPoint self;
        }

        public sealed class Params_Update
        {
            public StopAnimatorsAtPoint self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.SetCanStop_BeforeArgs SetCanStop
        {
            add
            {
                HookHandler._beforeSetCanStop += value;
                HookHandler.HookSetCanStop();
            }
            remove => HookHandler._beforeSetCanStop -= value;
        }

        public static event Delegates.SetCannotStop_BeforeArgs SetCannotStop
        {
            add
            {
                HookHandler._beforeSetCannotStop += value;
                HookHandler.HookSetCannotStop();
            }
            remove => HookHandler._beforeSetCannotStop -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
        {
            add
            {
                HookHandler._beforeUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._beforeUpdate -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Awake_NormalArgs Awake
        {
            add
            {
                HookHandler._afterAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._afterAwake -= value;
        }

        public static event Delegates.Start_NormalArgs Start
        {
            add
            {
                HookHandler._afterStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._afterStart -= value;
        }

        public static event Delegates.SetCanStop_NormalArgs SetCanStop
        {
            add
            {
                HookHandler._afterSetCanStop += value;
                HookHandler.HookSetCanStop();
            }
            remove => HookHandler._afterSetCanStop -= value;
        }

        public static event Delegates.SetCannotStop_NormalArgs SetCannotStop
        {
            add
            {
                HookHandler._afterSetCannotStop += value;
                HookHandler.HookSetCannotStop();
            }
            remove => HookHandler._afterSetCannotStop -= value;
        }

        public static event Delegates.Update_NormalArgs Update
        {
            add
            {
                HookHandler._afterUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._afterUpdate -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.StopAnimatorsAtPoint.hook_Awake Awake
        {
            add => On.StopAnimatorsAtPoint.Awake += value;
            remove => On.StopAnimatorsAtPoint.Awake -= value;
        }

        public static event On.StopAnimatorsAtPoint.hook_Start Start
        {
            add => On.StopAnimatorsAtPoint.Start += value;
            remove => On.StopAnimatorsAtPoint.Start -= value;
        }

        public static event On.StopAnimatorsAtPoint.hook_SetCanStop SetCanStop
        {
            add => On.StopAnimatorsAtPoint.SetCanStop += value;
            remove => On.StopAnimatorsAtPoint.SetCanStop -= value;
        }

        public static event On.StopAnimatorsAtPoint.hook_SetCannotStop SetCannotStop
        {
            add => On.StopAnimatorsAtPoint.SetCannotStop += value;
            remove => On.StopAnimatorsAtPoint.SetCannotStop -= value;
        }

        public static event On.StopAnimatorsAtPoint.hook_Update Update
        {
            add => On.StopAnimatorsAtPoint.Update += value;
            remove => On.StopAnimatorsAtPoint.Update -= value;
        }
    }
}