using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for SwitchSimpleInputDeviceManager class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSwitchSimpleInputDeviceManager
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hookget_Device()
        {
            if (!HookedList.Contains("get_Device"))
            {
                HookedList.Add("get_Device");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "get_Device"),
                    get_Device);
            }
        }

        internal static event Delegates.get_Device_BeforeArgs _beforeget_Device;
        internal static event Delegates.get_Device_NormalArgs _afterget_Device;

        private static SwitchSimpleInputDevice get_Device(
            Func<SwitchSimpleInputDeviceManager, SwitchSimpleInputDevice> orig, SwitchSimpleInputDeviceManager self)
        {
            Delegates.Params_get_Device @params = new()
            {
                self = self
            };
            _beforeget_Device?.Invoke(@params);
            self = @params.self;
            var retVal = orig(self);
            if (_afterget_Device != null) retVal = _afterget_Device.Invoke(@params);
            return retVal;
        }

        internal static void HookUpdate()
        {
            if (!HookedList.Contains("Update"))
            {
                HookedList.Add("Update");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "Update"), Update);
            }
        }

        internal static event Delegates.Update_BeforeArgs _beforeUpdate;
        internal static event Delegates.Update_NormalArgs _afterUpdate;

        private static void Update(Action<SwitchSimpleInputDeviceManager, ulong, float> orig,
            SwitchSimpleInputDeviceManager self, ulong updateTick, float deltaTime)
        {
            Delegates.Params_Update @params = new()
            {
                self = self, updateTick = updateTick, deltaTime = deltaTime
            };
            _beforeUpdate?.Invoke(@params);
            self = @params.self;
            updateTick = @params.updateTick;
            deltaTime = @params.deltaTime;
            orig(self, updateTick, deltaTime);
            if (_afterUpdate != null) _afterUpdate.Invoke(@params);
        }

        internal static void HookCheckPlatformSupport()
        {
            if (!HookedList.Contains("CheckPlatformSupport"))
            {
                HookedList.Add("CheckPlatformSupport");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "CheckPlatformSupport",
                        false), CheckPlatformSupport);
            }
        }

        internal static event Delegates.CheckPlatformSupport_BeforeArgs _beforeCheckPlatformSupport;
        internal static event Delegates.CheckPlatformSupport_NormalArgs _afterCheckPlatformSupport;

        private static bool CheckPlatformSupport(Func<ICollection<string>, bool> orig, ICollection<string> errors)
        {
            Delegates.Params_CheckPlatformSupport @params = new()
            {
                errors = errors
            };
            _beforeCheckPlatformSupport?.Invoke(@params);
            errors = @params.errors;
            var retVal = orig(errors);
            if (_afterCheckPlatformSupport != null) retVal = _afterCheckPlatformSupport.Invoke(@params);
            return retVal;
        }

        internal static void HookEnable()
        {
            if (!HookedList.Contains("Enable"))
            {
                HookedList.Add("Enable");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "Enable", false),
                    Enable);
            }
        }

        internal static event Delegates.Enable_BeforeArgs _beforeEnable;
        internal static event Delegates.Enable_NormalArgs _afterEnable;

        private static bool Enable(Func<bool> orig)
        {
            _beforeEnable?.Invoke();
            var retVal = orig();
            if (_afterEnable != null) retVal = _afterEnable.Invoke();
            return retVal;
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void CheckPlatformSupport_BeforeArgs(Params_CheckPlatformSupport args);

        public delegate bool CheckPlatformSupport_NormalArgs(Params_CheckPlatformSupport args);

        public delegate void Enable_BeforeArgs();

        public delegate bool Enable_NormalArgs();

        public delegate void get_Device_BeforeArgs(Params_get_Device args);

        public delegate SwitchSimpleInputDevice get_Device_NormalArgs(Params_get_Device args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_NormalArgs(Params_Update args);

        public sealed class Params_get_Device
        {
            public SwitchSimpleInputDeviceManager self;
        }

        public sealed class Params_Update
        {
            public float deltaTime;
            public SwitchSimpleInputDeviceManager self;
            public ulong updateTick;
        }

        public sealed class Params_CheckPlatformSupport
        {
            public ICollection<string> errors;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_Device_BeforeArgs get_Device
        {
            add
            {
                HookHandler._beforeget_Device += value;
                HookHandler.Hookget_Device();
            }
            remove => HookHandler._beforeget_Device -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
        {
            add
            {
                HookHandler._beforeUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._beforeUpdate -= value;
        }

        public static event Delegates.CheckPlatformSupport_BeforeArgs CheckPlatformSupport
        {
            add
            {
                HookHandler._beforeCheckPlatformSupport += value;
                HookHandler.HookCheckPlatformSupport();
            }
            remove => HookHandler._beforeCheckPlatformSupport -= value;
        }

        public static event Delegates.Enable_BeforeArgs Enable
        {
            add
            {
                HookHandler._beforeEnable += value;
                HookHandler.HookEnable();
            }
            remove => HookHandler._beforeEnable -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.get_Device_NormalArgs get_Device
        {
            add
            {
                HookHandler._afterget_Device += value;
                HookHandler.Hookget_Device();
            }
            remove => HookHandler._afterget_Device -= value;
        }

        public static event Delegates.Update_NormalArgs Update
        {
            add
            {
                HookHandler._afterUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._afterUpdate -= value;
        }

        public static event Delegates.CheckPlatformSupport_NormalArgs CheckPlatformSupport
        {
            add
            {
                HookHandler._afterCheckPlatformSupport += value;
                HookHandler.HookCheckPlatformSupport();
            }
            remove => HookHandler._afterCheckPlatformSupport -= value;
        }

        public static event Delegates.Enable_NormalArgs Enable
        {
            add
            {
                HookHandler._afterEnable += value;
                HookHandler.HookEnable();
            }
            remove => HookHandler._afterEnable -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_Device_NormalArgs get_Device
        {
            add => HookEndpointManager.Add<Delegates.get_Device_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "get_Device"), value);
            remove => HookEndpointManager.Remove<Delegates.get_Device_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "get_Device"), value);
        }

        public static event Delegates.Update_NormalArgs Update
        {
            add => HookEndpointManager.Add<Delegates.Update_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "Update"), value);
            remove => HookEndpointManager.Remove<Delegates.Update_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "Update"), value);
        }

        public static event Delegates.CheckPlatformSupport_NormalArgs CheckPlatformSupport
        {
            add => HookEndpointManager.Add<Delegates.CheckPlatformSupport_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "CheckPlatformSupport", false),
                value);
            remove => HookEndpointManager.Remove<Delegates.CheckPlatformSupport_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "CheckPlatformSupport", false),
                value);
        }

        public static event Delegates.Enable_NormalArgs Enable
        {
            add => HookEndpointManager.Add<Delegates.Enable_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "Enable", false), value);
            remove => HookEndpointManager.Remove<Delegates.Enable_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SwitchSimpleInputDeviceManager), "Enable", false), value);
        }
    }
}