using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for TMP_Glyph class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnTMP_Glyph
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookClone()
        {
            if (!HookedList.Contains("Clone"))
            {
                HookedList.Add("Clone");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(TMPro.TMP_Glyph), "Clone", false), Clone);
            }
        }

        internal static event Delegates.Clone_BeforeArgs _beforeClone;
        internal static event Delegates.Clone_AfterArgs _afterClone;

        private static TMPro.TMP_Glyph Clone(Func<TMPro.TMP_Glyph, TMPro.TMP_Glyph> orig, TMPro.TMP_Glyph source)
        {
            Delegates.Params_Clone @params = new()
            {
                source = source
            };
            if (_beforeClone != null)
            {
                foreach (Delegates.Clone_BeforeArgs toInvoke in _beforeClone.GetInvocationList())
                {
                    try
                    {
                        _beforeClone?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            source = @params.source;
            var retVal = orig(source);
            if (_afterClone != null)
            {
                foreach (Delegates.Clone_AfterArgs toInvoke in _afterClone.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterClone.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Clone
        {
            public TMPro.TMP_Glyph source;
        }

        public delegate void Clone_BeforeArgs(Params_Clone args);

        public delegate TMPro.TMP_Glyph Clone_AfterArgs(Params_Clone args, TMPro.TMP_Glyph ret);

        public delegate TMPro.TMP_Glyph Clone_WithArgs(Func<TMPro.TMP_Glyph, TMPro.TMP_Glyph> orig,
            TMPro.TMP_Glyph source);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Clone_BeforeArgs Clone
        {
            add
            {
                HookHandler._beforeClone += value;
                HookHandler.HookClone();
            }
            remove => HookHandler._beforeClone -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Clone_AfterArgs Clone
        {
            add
            {
                HookHandler._afterClone += value;
                HookHandler.HookClone();
            }
            remove => HookHandler._afterClone -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Clone_WithArgs Clone
        {
            add => HookEndpointManager.Add<Delegates.Clone_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.TMP_Glyph), "Clone", false), value);
            remove => HookEndpointManager.Remove<Delegates.Clone_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.TMP_Glyph), "Clone", false), value);
        }
    }
}