using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for Tk2dSpriteGetId class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnTk2dSpriteGetId
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hook_getSprite()
        {
            if (!HookedList.Contains("_getSprite"))
            {
                HookedList.Add("_getSprite");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "_getSprite"), _getSprite);
            }
        }

        internal static event Delegates._getSprite_BeforeArgs _before_getSprite;
        internal static event Delegates._getSprite_NormalArgs _after_getSprite;

        private static void _getSprite(Action<Tk2dSpriteGetId> orig, Tk2dSpriteGetId self)
        {
            Delegates.Params__getSprite @params = new()
            {
                self = self
            };
            _before_getSprite?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_after_getSprite != null) _after_getSprite.Invoke(@params);
        }

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<Tk2dSpriteGetId> orig, Tk2dSpriteGetId self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<Tk2dSpriteGetId> orig, Tk2dSpriteGetId self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookOnUpdate()
        {
            if (!HookedList.Contains("OnUpdate"))
            {
                HookedList.Add("OnUpdate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "OnUpdate"), OnUpdate);
            }
        }

        internal static event Delegates.OnUpdate_BeforeArgs _beforeOnUpdate;
        internal static event Delegates.OnUpdate_NormalArgs _afterOnUpdate;

        private static void OnUpdate(Action<Tk2dSpriteGetId> orig, Tk2dSpriteGetId self)
        {
            Delegates.Params_OnUpdate @params = new()
            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookDoGetSpriteID()
        {
            if (!HookedList.Contains("DoGetSpriteID"))
            {
                HookedList.Add("DoGetSpriteID");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "DoGetSpriteID"), DoGetSpriteID);
            }
        }

        internal static event Delegates.DoGetSpriteID_BeforeArgs _beforeDoGetSpriteID;
        internal static event Delegates.DoGetSpriteID_NormalArgs _afterDoGetSpriteID;

        private static void DoGetSpriteID(Action<Tk2dSpriteGetId> orig, Tk2dSpriteGetId self)
        {
            Delegates.Params_DoGetSpriteID @params = new()
            {
                self = self
            };
            _beforeDoGetSpriteID?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoGetSpriteID != null) _afterDoGetSpriteID.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void _getSprite_BeforeArgs(Params__getSprite args);

        public delegate void _getSprite_NormalArgs(Params__getSprite args);

        public delegate void DoGetSpriteID_BeforeArgs(Params_DoGetSpriteID args);

        public delegate void DoGetSpriteID_NormalArgs(Params_DoGetSpriteID args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_NormalArgs(Params_OnUpdate args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params__getSprite
        {
            public Tk2dSpriteGetId self;
        }

        public sealed class Params_Reset
        {
            public Tk2dSpriteGetId self;
        }

        public sealed class Params_OnEnter
        {
            public Tk2dSpriteGetId self;
        }

        public sealed class Params_OnUpdate
        {
            public Tk2dSpriteGetId self;
        }

        public sealed class Params_DoGetSpriteID
        {
            public Tk2dSpriteGetId self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates._getSprite_BeforeArgs _getSprite
        {
            add
            {
                HookHandler._before_getSprite += value;
                HookHandler.Hook_getSprite();
            }
            remove => HookHandler._before_getSprite -= value;
        }

        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._beforeOnUpdate -= value;
        }

        public static event Delegates.DoGetSpriteID_BeforeArgs DoGetSpriteID
        {
            add
            {
                HookHandler._beforeDoGetSpriteID += value;
                HookHandler.HookDoGetSpriteID();
            }
            remove => HookHandler._beforeDoGetSpriteID -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates._getSprite_NormalArgs _getSprite
        {
            add
            {
                HookHandler._after_getSprite += value;
                HookHandler.Hook_getSprite();
            }
            remove => HookHandler._after_getSprite -= value;
        }

        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add
            {
                HookHandler._afterOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._afterOnUpdate -= value;
        }

        public static event Delegates.DoGetSpriteID_NormalArgs DoGetSpriteID
        {
            add
            {
                HookHandler._afterDoGetSpriteID += value;
                HookHandler.HookDoGetSpriteID();
            }
            remove => HookHandler._afterDoGetSpriteID -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates._getSprite_NormalArgs _getSprite
        {
            add => HookEndpointManager.Add<Delegates._getSprite_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "_getSprite"), value);
            remove => HookEndpointManager.Remove<Delegates._getSprite_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "_getSprite"), value);
        }

        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "OnEnter"), value);
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add => HookEndpointManager.Add<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "OnUpdate"), value);
            remove => HookEndpointManager.Remove<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "OnUpdate"), value);
        }

        public static event Delegates.DoGetSpriteID_NormalArgs DoGetSpriteID
        {
            add => HookEndpointManager.Add<Delegates.DoGetSpriteID_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "DoGetSpriteID"), value);
            remove => HookEndpointManager.Remove<Delegates.DoGetSpriteID_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dSpriteGetId), "DoGetSpriteID"), value);
        }
    }
}