using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for Tk2dTextMeshSetFont class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnTk2dTextMeshSetFont
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hook_getTextMesh()
        {
            if (!HookedList.Contains("_getTextMesh"))
            {
                HookedList.Add("_getTextMesh");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "_getTextMesh"), _getTextMesh);
            }
        }

        internal static event Delegates._getTextMesh_BeforeArgs _before_getTextMesh;
        internal static event Delegates._getTextMesh_NormalArgs _after_getTextMesh;

        private static void _getTextMesh(Action<Tk2dTextMeshSetFont> orig, Tk2dTextMeshSetFont self)
        {
            Delegates.Params__getTextMesh @params = new()
            {
                self = self
            };
            _before_getTextMesh?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_after_getTextMesh != null) _after_getTextMesh.Invoke(@params);
        }

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<Tk2dTextMeshSetFont> orig, Tk2dTextMeshSetFont self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<Tk2dTextMeshSetFont> orig, Tk2dTextMeshSetFont self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoSetFont()
        {
            if (!HookedList.Contains("DoSetFont"))
            {
                HookedList.Add("DoSetFont");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "DoSetFont"), DoSetFont);
            }
        }

        internal static event Delegates.DoSetFont_BeforeArgs _beforeDoSetFont;
        internal static event Delegates.DoSetFont_NormalArgs _afterDoSetFont;

        private static void DoSetFont(Action<Tk2dTextMeshSetFont> orig, Tk2dTextMeshSetFont self)
        {
            Delegates.Params_DoSetFont @params = new()
            {
                self = self
            };
            _beforeDoSetFont?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoSetFont != null) _afterDoSetFont.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void _getTextMesh_BeforeArgs(Params__getTextMesh args);

        public delegate void _getTextMesh_NormalArgs(Params__getTextMesh args);

        public delegate void DoSetFont_BeforeArgs(Params_DoSetFont args);

        public delegate void DoSetFont_NormalArgs(Params_DoSetFont args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params__getTextMesh
        {
            public Tk2dTextMeshSetFont self;
        }

        public sealed class Params_Reset
        {
            public Tk2dTextMeshSetFont self;
        }

        public sealed class Params_OnEnter
        {
            public Tk2dTextMeshSetFont self;
        }

        public sealed class Params_DoSetFont
        {
            public Tk2dTextMeshSetFont self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates._getTextMesh_BeforeArgs _getTextMesh
        {
            add
            {
                HookHandler._before_getTextMesh += value;
                HookHandler.Hook_getTextMesh();
            }
            remove => HookHandler._before_getTextMesh -= value;
        }

        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoSetFont_BeforeArgs DoSetFont
        {
            add
            {
                HookHandler._beforeDoSetFont += value;
                HookHandler.HookDoSetFont();
            }
            remove => HookHandler._beforeDoSetFont -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates._getTextMesh_NormalArgs _getTextMesh
        {
            add
            {
                HookHandler._after_getTextMesh += value;
                HookHandler.Hook_getTextMesh();
            }
            remove => HookHandler._after_getTextMesh -= value;
        }

        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoSetFont_NormalArgs DoSetFont
        {
            add
            {
                HookHandler._afterDoSetFont += value;
                HookHandler.HookDoSetFont();
            }
            remove => HookHandler._afterDoSetFont -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates._getTextMesh_NormalArgs _getTextMesh
        {
            add => HookEndpointManager.Add<Delegates._getTextMesh_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "_getTextMesh"), value);
            remove => HookEndpointManager.Remove<Delegates._getTextMesh_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "_getTextMesh"), value);
        }

        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "OnEnter"), value);
        }

        public static event Delegates.DoSetFont_NormalArgs DoSetFont
        {
            add => HookEndpointManager.Add<Delegates.DoSetFont_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "DoSetFont"), value);
            remove => HookEndpointManager.Remove<Delegates.DoSetFont_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dTextMeshSetFont), "DoSetFont"), value);
        }
    }
}