using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for Tk2dWatchAnimationEvents class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnTk2dWatchAnimationEvents
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hook_getSprite()
        {
            if (!HookedList.Contains("_getSprite"))
            {
                HookedList.Add("_getSprite");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "_getSprite"), _getSprite);
            }
        }

        internal static event Delegates._getSprite_BeforeArgs _before_getSprite;
        internal static event Delegates._getSprite_NormalArgs _after_getSprite;

        private static void _getSprite(Action<Tk2dWatchAnimationEvents> orig, Tk2dWatchAnimationEvents self)
        {
            Delegates.Params__getSprite @params = new()
            {
                self = self
            };
            _before_getSprite?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_after_getSprite != null) _after_getSprite.Invoke(@params);
        }

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<Tk2dWatchAnimationEvents> orig, Tk2dWatchAnimationEvents self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<Tk2dWatchAnimationEvents> orig, Tk2dWatchAnimationEvents self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookOnUpdate()
        {
            if (!HookedList.Contains("OnUpdate"))
            {
                HookedList.Add("OnUpdate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "OnUpdate"), OnUpdate);
            }
        }

        internal static event Delegates.OnUpdate_BeforeArgs _beforeOnUpdate;
        internal static event Delegates.OnUpdate_NormalArgs _afterOnUpdate;

        private static void OnUpdate(Action<Tk2dWatchAnimationEvents> orig, Tk2dWatchAnimationEvents self)
        {
            Delegates.Params_OnUpdate @params = new()
            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookDoWatchAnimationWithEvents()
        {
            if (!HookedList.Contains("DoWatchAnimationWithEvents"))
            {
                HookedList.Add("DoWatchAnimationWithEvents");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "DoWatchAnimationWithEvents"),
                    DoWatchAnimationWithEvents);
            }
        }

        internal static event Delegates.DoWatchAnimationWithEvents_BeforeArgs _beforeDoWatchAnimationWithEvents;
        internal static event Delegates.DoWatchAnimationWithEvents_NormalArgs _afterDoWatchAnimationWithEvents;

        private static void DoWatchAnimationWithEvents(Action<Tk2dWatchAnimationEvents> orig,
            Tk2dWatchAnimationEvents self)
        {
            Delegates.Params_DoWatchAnimationWithEvents @params = new()
            {
                self = self
            };
            _beforeDoWatchAnimationWithEvents?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoWatchAnimationWithEvents != null) _afterDoWatchAnimationWithEvents.Invoke(@params);
        }

        internal static void HookAnimationEventDelegate()
        {
            if (!HookedList.Contains("AnimationEventDelegate"))
            {
                HookedList.Add("AnimationEventDelegate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "AnimationEventDelegate"),
                    AnimationEventDelegate);
            }
        }

        internal static event Delegates.AnimationEventDelegate_BeforeArgs _beforeAnimationEventDelegate;
        internal static event Delegates.AnimationEventDelegate_NormalArgs _afterAnimationEventDelegate;

        private static void AnimationEventDelegate(
            Action<Tk2dWatchAnimationEvents, tk2dSpriteAnimator, tk2dSpriteAnimationClip, int> orig,
            Tk2dWatchAnimationEvents self, tk2dSpriteAnimator sprite, tk2dSpriteAnimationClip clip, int frameNum)
        {
            Delegates.Params_AnimationEventDelegate @params = new()
            {
                self = self, sprite = sprite, clip = clip, frameNum = frameNum
            };
            _beforeAnimationEventDelegate?.Invoke(@params);
            self = @params.self;
            sprite = @params.sprite;
            clip = @params.clip;
            frameNum = @params.frameNum;
            orig(self, sprite, clip, frameNum);
            if (_afterAnimationEventDelegate != null) _afterAnimationEventDelegate.Invoke(@params);
        }

        internal static void HookAnimationCompleteDelegate()
        {
            if (!HookedList.Contains("AnimationCompleteDelegate"))
            {
                HookedList.Add("AnimationCompleteDelegate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "AnimationCompleteDelegate"),
                    AnimationCompleteDelegate);
            }
        }

        internal static event Delegates.AnimationCompleteDelegate_BeforeArgs _beforeAnimationCompleteDelegate;
        internal static event Delegates.AnimationCompleteDelegate_NormalArgs _afterAnimationCompleteDelegate;

        private static void AnimationCompleteDelegate(
            Action<Tk2dWatchAnimationEvents, tk2dSpriteAnimator, tk2dSpriteAnimationClip> orig,
            Tk2dWatchAnimationEvents self, tk2dSpriteAnimator sprite, tk2dSpriteAnimationClip clip)
        {
            Delegates.Params_AnimationCompleteDelegate @params = new()
            {
                self = self, sprite = sprite, clip = clip
            };
            _beforeAnimationCompleteDelegate?.Invoke(@params);
            self = @params.self;
            sprite = @params.sprite;
            clip = @params.clip;
            orig(self, sprite, clip);
            if (_afterAnimationCompleteDelegate != null) _afterAnimationCompleteDelegate.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void _getSprite_BeforeArgs(Params__getSprite args);

        public delegate void _getSprite_NormalArgs(Params__getSprite args);

        public delegate void AnimationCompleteDelegate_BeforeArgs(Params_AnimationCompleteDelegate args);

        public delegate void AnimationCompleteDelegate_NormalArgs(Params_AnimationCompleteDelegate args);

        public delegate void AnimationEventDelegate_BeforeArgs(Params_AnimationEventDelegate args);

        public delegate void AnimationEventDelegate_NormalArgs(Params_AnimationEventDelegate args);

        public delegate void DoWatchAnimationWithEvents_BeforeArgs(Params_DoWatchAnimationWithEvents args);

        public delegate void DoWatchAnimationWithEvents_NormalArgs(Params_DoWatchAnimationWithEvents args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_NormalArgs(Params_OnUpdate args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params__getSprite
        {
            public Tk2dWatchAnimationEvents self;
        }

        public sealed class Params_Reset
        {
            public Tk2dWatchAnimationEvents self;
        }

        public sealed class Params_OnEnter
        {
            public Tk2dWatchAnimationEvents self;
        }

        public sealed class Params_OnUpdate
        {
            public Tk2dWatchAnimationEvents self;
        }

        public sealed class Params_DoWatchAnimationWithEvents
        {
            public Tk2dWatchAnimationEvents self;
        }

        public sealed class Params_AnimationEventDelegate
        {
            public tk2dSpriteAnimationClip clip;
            public int frameNum;
            public Tk2dWatchAnimationEvents self;
            public tk2dSpriteAnimator sprite;
        }

        public sealed class Params_AnimationCompleteDelegate
        {
            public tk2dSpriteAnimationClip clip;
            public Tk2dWatchAnimationEvents self;
            public tk2dSpriteAnimator sprite;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates._getSprite_BeforeArgs _getSprite
        {
            add
            {
                HookHandler._before_getSprite += value;
                HookHandler.Hook_getSprite();
            }
            remove => HookHandler._before_getSprite -= value;
        }

        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._beforeOnUpdate -= value;
        }

        public static event Delegates.DoWatchAnimationWithEvents_BeforeArgs DoWatchAnimationWithEvents
        {
            add
            {
                HookHandler._beforeDoWatchAnimationWithEvents += value;
                HookHandler.HookDoWatchAnimationWithEvents();
            }
            remove => HookHandler._beforeDoWatchAnimationWithEvents -= value;
        }

        public static event Delegates.AnimationEventDelegate_BeforeArgs AnimationEventDelegate
        {
            add
            {
                HookHandler._beforeAnimationEventDelegate += value;
                HookHandler.HookAnimationEventDelegate();
            }
            remove => HookHandler._beforeAnimationEventDelegate -= value;
        }

        public static event Delegates.AnimationCompleteDelegate_BeforeArgs AnimationCompleteDelegate
        {
            add
            {
                HookHandler._beforeAnimationCompleteDelegate += value;
                HookHandler.HookAnimationCompleteDelegate();
            }
            remove => HookHandler._beforeAnimationCompleteDelegate -= value;
        }
    }

