    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates._getSprite_NormalArgs _getSprite
        {
            add => HookEndpointManager.Add<Delegates._getSprite_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "_getSprite"), value);
            remove => HookEndpointManager.Remove<Delegates._getSprite_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "_getSprite"), value);
        }

        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "OnEnter"), value);
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add => HookEndpointManager.Add<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "OnUpdate"), value);
            remove => HookEndpointManager.Remove<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "OnUpdate"), value);
        }

        public static event Delegates.DoWatchAnimationWithEvents_NormalArgs DoWatchAnimationWithEvents
        {
            add => HookEndpointManager.Add<Delegates.DoWatchAnimationWithEvents_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "DoWatchAnimationWithEvents"), value);
            remove => HookEndpointManager.Remove<Delegates.DoWatchAnimationWithEvents_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "DoWatchAnimationWithEvents"), value);
        }

        public static event Delegates.AnimationEventDelegate_NormalArgs AnimationEventDelegate
        {
            add => HookEndpointManager.Add<Delegates.AnimationEventDelegate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "AnimationEventDelegate"), value);
            remove => HookEndpointManager.Remove<Delegates.AnimationEventDelegate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "AnimationEventDelegate"), value);
        }

        public static event Delegates.AnimationCompleteDelegate_NormalArgs AnimationCompleteDelegate
        {
            add => HookEndpointManager.Add<Delegates.AnimationCompleteDelegate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "AnimationCompleteDelegate"), value);
            remove => HookEndpointManager.Remove<Delegates.AnimationCompleteDelegate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(Tk2dWatchAnimationEvents), "AnimationCompleteDelegate"), value);
        }
    }
