/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_Start
{
public WaveEffectControl self;
}
public delegate void Start_BeforeArgs(Params_Start args);
public delegate void Start_AfterArgs(Params_Start args);
public delegate void Start_WithArgs (Action<WaveEffectControl> orig, WaveEffectControl self);
public sealed class Params_OnEnable
{
public WaveEffectControl self;
}
public delegate void OnEnable_BeforeArgs(Params_OnEnable args);
public delegate void OnEnable_AfterArgs(Params_OnEnable args);
public delegate void OnEnable_WithArgs (Action<WaveEffectControl> orig, WaveEffectControl self);
public sealed class Params_Update
{
public WaveEffectControl self;
}
public delegate void Update_BeforeArgs(Params_Update args);
public delegate void Update_AfterArgs(Params_Update args);
public delegate void Update_WithArgs (Action<WaveEffectControl> orig, WaveEffectControl self);
public sealed class Params_FixedUpdate
{
public WaveEffectControl self;
}
public delegate void FixedUpdate_BeforeArgs(Params_FixedUpdate args);
public delegate void FixedUpdate_AfterArgs(Params_FixedUpdate args);
public delegate void FixedUpdate_WithArgs (Action<WaveEffectControl> orig, WaveEffectControl self);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.Start_BeforeArgs Start
{
add
{

HookHandler._beforeStart += value;
HookHandler.HookStart();
}
remove => HookHandler._beforeStart -= value;
}
public static event Delegates.OnEnable_BeforeArgs OnEnable
{
add
{

HookHandler._beforeOnEnable += value;
HookHandler.HookOnEnable();
}
remove => HookHandler._beforeOnEnable -= value;
}
public static event Delegates.Update_BeforeArgs Update
{
