/// <summary>
/// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
/// </summary>
public static class WithOrig
{
public static event On.WaveEffectControl.hook_Start Start
{
add => On.WaveEffectControl.Start += value;
remove => On.WaveEffectControl.Start -= value;
}
public static event On.WaveEffectControl.hook_OnEnable OnEnable
{
