using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for XInputDevice class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnXInputDevice
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hookget_DeviceIndex()
        {
            if (!HookedList.Contains("get_DeviceIndex"))
            {
                HookedList.Add("get_DeviceIndex");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "get_DeviceIndex"), get_DeviceIndex);
            }
        }

        internal static event Delegates.get_DeviceIndex_BeforeArgs _beforeget_DeviceIndex;
        internal static event Delegates.get_DeviceIndex_NormalArgs _afterget_DeviceIndex;

        private static int get_DeviceIndex(Func<XInputDevice, int> orig, XInputDevice self)
        {
            Delegates.Params_get_DeviceIndex @params = new()
            {
                self = self
            };
            _beforeget_DeviceIndex?.Invoke(@params);
            self = @params.self;
            var retVal = orig(self);
            if (_afterget_DeviceIndex != null) retVal = _afterget_DeviceIndex.Invoke(@params);
            return retVal;
        }

        internal static void Hookset_DeviceIndex()
        {
            if (!HookedList.Contains("set_DeviceIndex"))
            {
                HookedList.Add("set_DeviceIndex");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "set_DeviceIndex"), set_DeviceIndex);
            }
        }

        internal static event Delegates.set_DeviceIndex_BeforeArgs _beforeset_DeviceIndex;
        internal static event Delegates.set_DeviceIndex_NormalArgs _afterset_DeviceIndex;

        private static void set_DeviceIndex(Action<XInputDevice, int> orig, XInputDevice self, int value)
        {
            Delegates.Params_set_DeviceIndex @params = new()
            {
                self = self, value = value
            };
            _beforeset_DeviceIndex?.Invoke(@params);
            self = @params.self;
            value = @params.value;
            orig(self, value);
            if (_afterset_DeviceIndex != null) _afterset_DeviceIndex.Invoke(@params);
        }

        internal static void HookUpdate()
        {
            if (!HookedList.Contains("Update"))
            {
                HookedList.Add("Update");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "Update"), Update);
            }
        }

        internal static event Delegates.Update_BeforeArgs _beforeUpdate;
        internal static event Delegates.Update_NormalArgs _afterUpdate;

        private static void Update(Action<XInputDevice, ulong, float> orig, XInputDevice self, ulong updateTick,
            float deltaTime)
        {
            Delegates.Params_Update @params = new()
            {
                self = self, updateTick = updateTick, deltaTime = deltaTime
            };
            _beforeUpdate?.Invoke(@params);
            self = @params.self;
            updateTick = @params.updateTick;
            deltaTime = @params.deltaTime;
            orig(self, updateTick, deltaTime);
            if (_afterUpdate != null) _afterUpdate.Invoke(@params);
        }

        internal static void HookVibrate()
        {
            if (!HookedList.Contains("Vibrate"))
            {
                HookedList.Add("Vibrate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "Vibrate"), Vibrate);
            }
        }

        internal static event Delegates.Vibrate_BeforeArgs _beforeVibrate;
        internal static event Delegates.Vibrate_NormalArgs _afterVibrate;

        private static void Vibrate(Action<XInputDevice, float, float> orig, XInputDevice self, float leftMotor,
            float rightMotor)
        {
            Delegates.Params_Vibrate @params = new()
            {
                self = self, leftMotor = leftMotor, rightMotor = rightMotor
            };
            _beforeVibrate?.Invoke(@params);
            self = @params.self;
            leftMotor = @params.leftMotor;
            rightMotor = @params.rightMotor;
            orig(self, leftMotor, rightMotor);
            if (_afterVibrate != null) _afterVibrate.Invoke(@params);
        }

        internal static void HookGetState()
        {
            if (!HookedList.Contains("GetState"))
            {
                HookedList.Add("GetState");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "GetState"), GetState);
            }
        }

        internal static event Delegates.GetState_BeforeArgs _beforeGetState;
        internal static event Delegates.GetState_NormalArgs _afterGetState;

        private static void GetState(Action<XInputDevice> orig, XInputDevice self)
        {
            Delegates.Params_GetState @params = new()
            {
                self = self
            };
            _beforeGetState?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterGetState != null) _afterGetState.Invoke(@params);
        }

        internal static void Hookget_IsConnected()
        {
            if (!HookedList.Contains("get_IsConnected"))
            {
                HookedList.Add("get_IsConnected");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "get_IsConnected"), get_IsConnected);
            }
        }

        internal static event Delegates.get_IsConnected_BeforeArgs _beforeget_IsConnected;
        internal static event Delegates.get_IsConnected_NormalArgs _afterget_IsConnected;

        private static bool get_IsConnected(Func<XInputDevice, bool> orig, XInputDevice self)
        {
            Delegates.Params_get_IsConnected @params = new()
            {
                self = self
            };
            _beforeget_IsConnected?.Invoke(@params);
            self = @params.self;
            var retVal = orig(self);
            if (_afterget_IsConnected != null) retVal = _afterget_IsConnected.Invoke(@params);
            return retVal;
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void get_DeviceIndex_BeforeArgs(Params_get_DeviceIndex args);

        public delegate int get_DeviceIndex_NormalArgs(Params_get_DeviceIndex args);

        public delegate void get_IsConnected_BeforeArgs(Params_get_IsConnected args);

        public delegate bool get_IsConnected_NormalArgs(Params_get_IsConnected args);

        public delegate void GetState_BeforeArgs(Params_GetState args);

        public delegate void GetState_NormalArgs(Params_GetState args);

        public delegate void set_DeviceIndex_BeforeArgs(Params_set_DeviceIndex args);

        public delegate void set_DeviceIndex_NormalArgs(Params_set_DeviceIndex args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_NormalArgs(Params_Update args);

        public delegate void Vibrate_BeforeArgs(Params_Vibrate args);

        public delegate void Vibrate_NormalArgs(Params_Vibrate args);

        public sealed class Params_get_DeviceIndex
        {
            public XInputDevice self;
        }

        public sealed class Params_set_DeviceIndex
        {
            public XInputDevice self;
            public int value;
        }

        public sealed class Params_Update
        {
            public float deltaTime;
            public XInputDevice self;
            public ulong updateTick;
        }

        public sealed class Params_Vibrate
        {
            public float leftMotor;
            public float rightMotor;
            public XInputDevice self;
        }

        public sealed class Params_GetState
        {
            public XInputDevice self;
        }

        public sealed class Params_get_IsConnected
        {
            public XInputDevice self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_DeviceIndex_BeforeArgs get_DeviceIndex
        {
            add
            {
                HookHandler._beforeget_DeviceIndex += value;
                HookHandler.Hookget_DeviceIndex();
            }
            remove => HookHandler._beforeget_DeviceIndex -= value;
        }

        public static event Delegates.set_DeviceIndex_BeforeArgs set_DeviceIndex
        {
            add
            {
                HookHandler._beforeset_DeviceIndex += value;
                HookHandler.Hookset_DeviceIndex();
            }
            remove => HookHandler._beforeset_DeviceIndex -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
        {
            add
            {
                HookHandler._beforeUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._beforeUpdate -= value;
        }

        public static event Delegates.Vibrate_BeforeArgs Vibrate
        {
            add
            {
                HookHandler._beforeVibrate += value;
                HookHandler.HookVibrate();
            }
            remove => HookHandler._beforeVibrate -= value;
        }

        public static event Delegates.GetState_BeforeArgs GetState
        {
            add
            {
                HookHandler._beforeGetState += value;
                HookHandler.HookGetState();
            }
            remove => HookHandler._beforeGetState -= value;
        }

        public static event Delegates.get_IsConnected_BeforeArgs get_IsConnected
        {
            add
            {
                HookHandler._beforeget_IsConnected += value;
                HookHandler.Hookget_IsConnected();
            }
            remove => HookHandler._beforeget_IsConnected -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.get_DeviceIndex_NormalArgs get_DeviceIndex
        {
            add
            {
                HookHandler._afterget_DeviceIndex += value;
                HookHandler.Hookget_DeviceIndex();
            }
            remove => HookHandler._afterget_DeviceIndex -= value;
        }

        public static event Delegates.set_DeviceIndex_NormalArgs set_DeviceIndex
        {
            add
            {
                HookHandler._afterset_DeviceIndex += value;
                HookHandler.Hookset_DeviceIndex();
            }
            remove => HookHandler._afterset_DeviceIndex -= value;
        }

        public static event Delegates.Update_NormalArgs Update
        {
            add
            {
                HookHandler._afterUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._afterUpdate -= value;
        }

        public static event Delegates.Vibrate_NormalArgs Vibrate
        {
            add
            {
                HookHandler._afterVibrate += value;
                HookHandler.HookVibrate();
            }
            remove => HookHandler._afterVibrate -= value;
        }

        public static event Delegates.GetState_NormalArgs GetState
        {
            add
            {
                HookHandler._afterGetState += value;
                HookHandler.HookGetState();
            }
            remove => HookHandler._afterGetState -= value;
        }

        public static event Delegates.get_IsConnected_NormalArgs get_IsConnected
        {
            add
            {
                HookHandler._afterget_IsConnected += value;
                HookHandler.Hookget_IsConnected();
            }
            remove => HookHandler._afterget_IsConnected -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_DeviceIndex_NormalArgs get_DeviceIndex
        {
            add => HookEndpointManager.Add<Delegates.get_DeviceIndex_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "get_DeviceIndex"), value);
            remove => HookEndpointManager.Remove<Delegates.get_DeviceIndex_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "get_DeviceIndex"), value);
        }

        public static event Delegates.set_DeviceIndex_NormalArgs set_DeviceIndex
        {
            add => HookEndpointManager.Add<Delegates.set_DeviceIndex_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "set_DeviceIndex"), value);
            remove => HookEndpointManager.Remove<Delegates.set_DeviceIndex_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "set_DeviceIndex"), value);
        }

        public static event Delegates.Update_NormalArgs Update
        {
            add => HookEndpointManager.Add<Delegates.Update_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "Update"), value);
            remove => HookEndpointManager.Remove<Delegates.Update_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "Update"), value);
        }

        public static event Delegates.Vibrate_NormalArgs Vibrate
        {
            add => HookEndpointManager.Add<Delegates.Vibrate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "Vibrate"), value);
            remove => HookEndpointManager.Remove<Delegates.Vibrate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "Vibrate"), value);
        }

        public static event Delegates.GetState_NormalArgs GetState
        {
            add => HookEndpointManager.Add<Delegates.GetState_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "GetState"), value);
            remove => HookEndpointManager.Remove<Delegates.GetState_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "GetState"), value);
        }

        public static event Delegates.get_IsConnected_NormalArgs get_IsConnected
        {
            add => HookEndpointManager.Add<Delegates.get_IsConnected_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "get_IsConnected"), value);
            remove => HookEndpointManager.Remove<Delegates.get_IsConnected_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(XInputDevice), "get_IsConnected"), value);
        }
    }
}