{
self = self
};
if (_beforeOnEnter != null)
{
foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
{
try
{
_beforeOnEnter?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterOnEnter != null)
{
foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
{
try
{
_afterOnEnter.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookDoiTween()
{
if (!HookedList.Contains("DoiTween"))
{
HookedList.Add("DoiTween");
new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.iTweenPause), "DoiTween", true), DoiTween);

}
}
internal static event Delegates.DoiTween_BeforeArgs _beforeDoiTween;
internal static event Delegates.DoiTween_AfterArgs _afterDoiTween;
private static void DoiTween(Action<HutongGames.PlayMaker.Actions.iTweenPause> orig, HutongGames.PlayMaker.Actions.iTweenPause self)
{
Delegates.Params_DoiTween @params = new() 
{
