};
if (_beforeDoiTween != null)
{
foreach (Delegates.DoiTween_BeforeArgs toInvoke in _beforeDoiTween.GetInvocationList())
{
try
{
_beforeDoiTween?.Invoke(@params);
}
catch (Exception e) 
