try
{
_afterDoiTween.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
}

/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_Reset
{
public HutongGames.PlayMaker.Actions.iTweenPause self;
}
public delegate void Reset_BeforeArgs(Params_Reset args);
public delegate void Reset_AfterArgs(Params_Reset args);
public delegate void Reset_WithArgs (Action<HutongGames.PlayMaker.Actions.iTweenPause> orig, HutongGames.PlayMaker.Actions.iTweenPause self);
public sealed class Params_OnEnter
{
public HutongGames.PlayMaker.Actions.iTweenPause self;
}
public delegate void OnEnter_BeforeArgs(Params_OnEnter args);
public delegate void OnEnter_AfterArgs(Params_OnEnter args);
public delegate void OnEnter_WithArgs (Action<HutongGames.PlayMaker.Actions.iTweenPause> orig, HutongGames.PlayMaker.Actions.iTweenPause self);
public sealed class Params_DoiTween
{
public HutongGames.PlayMaker.Actions.iTweenPause self;
}
public delegate void DoiTween_BeforeArgs(Params_DoiTween args);
public delegate void DoiTween_AfterArgs(Params_DoiTween args);
public delegate void DoiTween_WithArgs (Action<HutongGames.PlayMaker.Actions.iTweenPause> orig, HutongGames.PlayMaker.Actions.iTweenPause self);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.Reset_BeforeArgs Reset
{
add
{

HookHandler._beforeReset += value;
HookHandler.HookReset();
}
remove => HookHandler._beforeReset -= value;
}
public static event Delegates.OnEnter_BeforeArgs OnEnter
{
add
{

HookHandler._beforeOnEnter += value;
HookHandler.HookOnEnter();
}
remove => HookHandler._beforeOnEnter -= value;
}
public static event Delegates.DoiTween_BeforeArgs DoiTween
{
add
{

HookHandler._beforeDoiTween += value;
HookHandler.HookDoiTween();
}
remove => HookHandler._beforeDoiTween -= value;
}
}

