using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for tk2dSpriteCollectionFont class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class Ontk2dSpriteCollectionFont
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookCopyFrom()
        {
            if (!HookedList.Contains("CopyFrom"))
            {
                HookedList.Add("CopyFrom");
                On.tk2dSpriteCollectionFont.CopyFrom += CopyFrom;
            }
        }

        internal static event Delegates.CopyFrom_BeforeArgs _beforeCopyFrom;
        internal static event Delegates.CopyFrom_AfterArgs _afterCopyFrom;

        private static void CopyFrom(On.tk2dSpriteCollectionFont.orig_CopyFrom orig, tk2dSpriteCollectionFont self,
            tk2dSpriteCollectionFont src)
        {
            Delegates.Params_CopyFrom @params = new()
            {
                self = self, src = src
            };
            if (_beforeCopyFrom != null)
            {
                foreach (Delegates.CopyFrom_BeforeArgs toInvoke in _beforeCopyFrom.GetInvocationList())
                {
                    try
                    {
                        _beforeCopyFrom?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            src = @params.src;
            orig(self, src);
            if (_afterCopyFrom != null)
            {
                foreach (Delegates.CopyFrom_AfterArgs toInvoke in _afterCopyFrom.GetInvocationList())
                {
                    try
                    {
                        _afterCopyFrom.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void Hookget_Name()
        {
            if (!HookedList.Contains("get_Name"))
            {
                HookedList.Add("get_Name");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteCollectionFont), "get_Name", true), get_Name);
            }
        }

        internal static event Delegates.get_Name_BeforeArgs _beforeget_Name;
        internal static event Delegates.get_Name_AfterArgs _afterget_Name;

        private static string get_Name(Func<tk2dSpriteCollectionFont, string> orig, tk2dSpriteCollectionFont self)
        {
            Delegates.Params_get_Name @params = new()
            {
                self = self
            };
            if (_beforeget_Name != null)
            {
                foreach (Delegates.get_Name_BeforeArgs toInvoke in _beforeget_Name.GetInvocationList())
                {
                    try
                    {
                        _beforeget_Name?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_Name != null)
            {
                foreach (Delegates.get_Name_AfterArgs toInvoke in _afterget_Name.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_Name.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

        internal static void Hookget_InUse()
        {
            if (!HookedList.Contains("get_InUse"))
            {
                HookedList.Add("get_InUse");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteCollectionFont), "get_InUse", true),
                    get_InUse);
            }
        }

        internal static event Delegates.get_InUse_BeforeArgs _beforeget_InUse;
        internal static event Delegates.get_InUse_AfterArgs _afterget_InUse;

        private static bool get_InUse(Func<tk2dSpriteCollectionFont, bool> orig, tk2dSpriteCollectionFont self)
        {
            Delegates.Params_get_InUse @params = new()
            {
                self = self
            };
            if (_beforeget_InUse != null)
            {
                foreach (Delegates.get_InUse_BeforeArgs toInvoke in _beforeget_InUse.GetInvocationList())
                {
                    try
                    {
                        _beforeget_InUse?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_InUse != null)
            {
                foreach (Delegates.get_InUse_AfterArgs toInvoke in _afterget_InUse.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_InUse.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_CopyFrom
        {
            public tk2dSpriteCollectionFont self;
            public tk2dSpriteCollectionFont src;
        }

        public delegate void CopyFrom_BeforeArgs(Params_CopyFrom args);

        public delegate void CopyFrom_AfterArgs(Params_CopyFrom args);

        public delegate void CopyFrom_WithArgs(Action<tk2dSpriteCollectionFont, tk2dSpriteCollectionFont> orig,
            tk2dSpriteCollectionFont self, tk2dSpriteCollectionFont src);

        public sealed class Params_get_Name
        {
            public tk2dSpriteCollectionFont self;
        }

        public delegate void get_Name_BeforeArgs(Params_get_Name args);

        public delegate string get_Name_AfterArgs(Params_get_Name args, string ret);

        public delegate string get_Name_WithArgs(Func<tk2dSpriteCollectionFont, string> orig,
            tk2dSpriteCollectionFont self);

        public sealed class Params_get_InUse
        {
            public tk2dSpriteCollectionFont self;
        }

        public delegate void get_InUse_BeforeArgs(Params_get_InUse args);

        public delegate bool get_InUse_AfterArgs(Params_get_InUse args, bool ret);

        public delegate bool get_InUse_WithArgs(Func<tk2dSpriteCollectionFont, bool> orig,
            tk2dSpriteCollectionFont self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.CopyFrom_BeforeArgs CopyFrom
        {
            add
            {
                HookHandler._beforeCopyFrom += value;
                HookHandler.HookCopyFrom();
            }
            remove => HookHandler._beforeCopyFrom -= value;
        }

        public static event Delegates.get_Name_BeforeArgs get_Name
        {
            add
            {
                HookHandler._beforeget_Name += value;
                HookHandler.Hookget_Name();
            }
            remove => HookHandler._beforeget_Name -= value;
        }

        public static event Delegates.get_InUse_BeforeArgs get_InUse
        {
            add
            {
                HookHandler._beforeget_InUse += value;
                HookHandler.Hookget_InUse();
            }
            remove => HookHandler._beforeget_InUse -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.CopyFrom_AfterArgs CopyFrom
        {
            add
            {
                HookHandler._afterCopyFrom += value;
                HookHandler.HookCopyFrom();
            }
            remove => HookHandler._afterCopyFrom -= value;
        }

        public static event Delegates.get_Name_AfterArgs get_Name
        {
            add
            {
                HookHandler._afterget_Name += value;
                HookHandler.Hookget_Name();
            }
            remove => HookHandler._afterget_Name -= value;
        }

        public static event Delegates.get_InUse_AfterArgs get_InUse
        {
            add
            {
                HookHandler._afterget_InUse += value;
                HookHandler.Hookget_InUse();
            }
            remove => HookHandler._afterget_InUse -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.tk2dSpriteCollectionFont.hook_CopyFrom CopyFrom
        {
            add => On.tk2dSpriteCollectionFont.CopyFrom += value;
            remove => On.tk2dSpriteCollectionFont.CopyFrom -= value;
        }

        public static event Delegates.get_Name_WithArgs get_Name
        {
            add => HookEndpointManager.Add<Delegates.get_Name_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteCollectionFont), "get_Name", true), value);
            remove => HookEndpointManager.Remove<Delegates.get_Name_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteCollectionFont), "get_Name", true), value);
        }

        public static event Delegates.get_InUse_WithArgs get_InUse
        {
            add => HookEndpointManager.Add<Delegates.get_InUse_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteCollectionFont), "get_InUse", true), value);
            remove => HookEndpointManager.Remove<Delegates.get_InUse_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteCollectionFont), "get_InUse", true), value);
        }
    }
}