using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for tk2dSpriteColliderDefinition class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class Ontk2dSpriteColliderDefinition
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void Hookget_Radius()
        {
            if (!HookedList.Contains("get_Radius"))
            {
                HookedList.Add("get_Radius");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteColliderDefinition), "get_Radius", true),
                    get_Radius);
            }
        }

        internal static event Delegates.get_Radius_BeforeArgs _beforeget_Radius;
        internal static event Delegates.get_Radius_AfterArgs _afterget_Radius;

        private static float get_Radius(Func<tk2dSpriteColliderDefinition, float> orig,
            tk2dSpriteColliderDefinition self)
        {
            Delegates.Params_get_Radius @params = new()
            {
                self = self
            };
            if (_beforeget_Radius != null)
            {
                foreach (Delegates.get_Radius_BeforeArgs toInvoke in _beforeget_Radius.GetInvocationList())
                {
                    try
                    {
                        _beforeget_Radius?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_Radius != null)
            {
                foreach (Delegates.get_Radius_AfterArgs toInvoke in _afterget_Radius.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_Radius.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

        internal static void Hookget_Size()
        {
            if (!HookedList.Contains("get_Size"))
            {
                HookedList.Add("get_Size");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteColliderDefinition), "get_Size", true),
                    get_Size);
            }
        }

        internal static event Delegates.get_Size_BeforeArgs _beforeget_Size;
        internal static event Delegates.get_Size_AfterArgs _afterget_Size;

        private static UnityEngine.Vector3 get_Size(Func<tk2dSpriteColliderDefinition, UnityEngine.Vector3> orig,
            tk2dSpriteColliderDefinition self)
        {
            Delegates.Params_get_Size @params = new()
            {
                self = self
            };
            if (_beforeget_Size != null)
            {
                foreach (Delegates.get_Size_BeforeArgs toInvoke in _beforeget_Size.GetInvocationList())
                {
                    try
                    {
                        _beforeget_Size?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_Size != null)
            {
                foreach (Delegates.get_Size_AfterArgs toInvoke in _afterget_Size.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_Size.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_get_Radius
        {
            public tk2dSpriteColliderDefinition self;
        }

        public delegate void get_Radius_BeforeArgs(Params_get_Radius args);

        public delegate float get_Radius_AfterArgs(Params_get_Radius args, float ret);

        public delegate float get_Radius_WithArgs(Func<tk2dSpriteColliderDefinition, float> orig,
            tk2dSpriteColliderDefinition self);

        public sealed class Params_get_Size
        {
            public tk2dSpriteColliderDefinition self;
        }

        public delegate void get_Size_BeforeArgs(Params_get_Size args);

        public delegate UnityEngine.Vector3 get_Size_AfterArgs(Params_get_Size args, UnityEngine.Vector3 ret);

        public delegate UnityEngine.Vector3 get_Size_WithArgs(
            Func<tk2dSpriteColliderDefinition, UnityEngine.Vector3> orig, tk2dSpriteColliderDefinition self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_Radius_BeforeArgs get_Radius
        {
            add
            {
                HookHandler._beforeget_Radius += value;
                HookHandler.Hookget_Radius();
            }
            remove => HookHandler._beforeget_Radius -= value;
        }

        public static event Delegates.get_Size_BeforeArgs get_Size
        {
            add
            {
                HookHandler._beforeget_Size += value;
                HookHandler.Hookget_Size();
            }
            remove => HookHandler._beforeget_Size -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.get_Radius_AfterArgs get_Radius
        {
            add
            {
                HookHandler._afterget_Radius += value;
                HookHandler.Hookget_Radius();
            }
            remove => HookHandler._afterget_Radius -= value;
        }

        public static event Delegates.get_Size_AfterArgs get_Size
        {
            add
            {
                HookHandler._afterget_Size += value;
                HookHandler.Hookget_Size();
            }
            remove => HookHandler._afterget_Size -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_Radius_WithArgs get_Radius
        {
            add => HookEndpointManager.Add<Delegates.get_Radius_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteColliderDefinition), "get_Radius", true), value);
            remove => HookEndpointManager.Remove<Delegates.get_Radius_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteColliderDefinition), "get_Radius", true), value);
        }

        public static event Delegates.get_Size_WithArgs get_Size
        {
            add => HookEndpointManager.Add<Delegates.get_Size_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteColliderDefinition), "get_Size", true), value);
            remove => HookEndpointManager.Remove<Delegates.get_Size_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteColliderDefinition), "get_Size", true), value);
        }
    }
}