using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;
namespace HKMirror.Hooks.OnHooks;
/// <summary>
/// Contains different types of On Hooks for tk2dSpriteDefinition_AttachPoint class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class Ontk2dSpriteDefinition_AttachPoint
{
internal static class HookHandler
{
private static List<string> HookedList = new List<string>();
internal static void HookCopyFrom()
{
if (!HookedList.Contains("CopyFrom"))
{
HookedList.Add("CopyFrom");
new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteDefinition.AttachPoint), "CopyFrom", true), CopyFrom);

}
}
internal static event Delegates.CopyFrom_BeforeArgs _beforeCopyFrom;
internal static event Delegates.CopyFrom_AfterArgs _afterCopyFrom;
private static void CopyFrom(Action<tk2dSpriteDefinition.AttachPoint,  tk2dSpriteDefinition.AttachPoint> orig, tk2dSpriteDefinition.AttachPoint self,  tk2dSpriteDefinition.AttachPoint src)
{
Delegates.Params_CopyFrom @params = new() 
{
self = self
, src = src
};
if (_beforeCopyFrom != null)
{
foreach (Delegates.CopyFrom_BeforeArgs toInvoke in _beforeCopyFrom.GetInvocationList())
{
try
{
_beforeCopyFrom?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
src = @params.src;
orig(self, src);
if (_afterCopyFrom != null)
{
foreach (Delegates.CopyFrom_AfterArgs toInvoke in _afterCopyFrom.GetInvocationList())
{
try
{
_afterCopyFrom.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookCompareTo()
{
if (!HookedList.Contains("CompareTo"))
{
HookedList.Add("CompareTo");
new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteDefinition.AttachPoint), "CompareTo", true), CompareTo);

}
}
internal static event Delegates.CompareTo_BeforeArgs _beforeCompareTo;
internal static event Delegates.CompareTo_AfterArgs _afterCompareTo;
private static bool CompareTo(Func<tk2dSpriteDefinition.AttachPoint,  tk2dSpriteDefinition.AttachPoint, bool> orig, tk2dSpriteDefinition.AttachPoint self,  tk2dSpriteDefinition.AttachPoint src)
{
Delegates.Params_CompareTo @params = new() 
{
self = self
, src = src
};
if (_beforeCompareTo != null)
{
foreach (Delegates.CompareTo_BeforeArgs toInvoke in _beforeCompareTo.GetInvocationList())
{
try
{
_beforeCompareTo?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
src = @params.src;
var retVal = orig(self, src);
if (_afterCompareTo != null)
{
foreach (Delegates.CompareTo_AfterArgs toInvoke in _afterCompareTo.GetInvocationList())
{
try
{
retVal = _afterCompareTo.Invoke(@params, retVal);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
return retVal;
}
}

/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_CopyFrom
{
public tk2dSpriteDefinition.AttachPoint self;
public tk2dSpriteDefinition.AttachPoint src;
}
public delegate void CopyFrom_BeforeArgs(Params_CopyFrom args);
public delegate void CopyFrom_AfterArgs(Params_CopyFrom args);
public delegate void CopyFrom_WithArgs (Action<tk2dSpriteDefinition.AttachPoint,  tk2dSpriteDefinition.AttachPoint> orig, tk2dSpriteDefinition.AttachPoint self,  tk2dSpriteDefinition.AttachPoint src);
public sealed class Params_CompareTo
{
public tk2dSpriteDefinition.AttachPoint self;
public tk2dSpriteDefinition.AttachPoint src;
}
public delegate void CompareTo_BeforeArgs(Params_CompareTo args);
public delegate bool CompareTo_AfterArgs(Params_CompareTo args, bool ret);
public delegate bool CompareTo_WithArgs (Func<tk2dSpriteDefinition.AttachPoint,  tk2dSpriteDefinition.AttachPoint, bool> orig, tk2dSpriteDefinition.AttachPoint self,  tk2dSpriteDefinition.AttachPoint src);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.CopyFrom_BeforeArgs CopyFrom
{
add
{

HookHandler._beforeCopyFrom += value;
HookHandler.HookCopyFrom();
}
remove => HookHandler._beforeCopyFrom -= value;
}
public static event Delegates.CompareTo_BeforeArgs CompareTo
{
add
{

HookHandler._beforeCompareTo += value;
HookHandler.HookCompareTo();
}
remove => HookHandler._beforeCompareTo -= value;
}
}

/// <summary>
/// Contains Hooks to that run code after orig(self) is called
/// </summary>
public static class AfterOrig
{
public static event Delegates.CopyFrom_AfterArgs CopyFrom
{
add
{

HookHandler._afterCopyFrom += value;
HookHandler.HookCopyFrom();
}
remove => HookHandler._afterCopyFrom -= value;
}
public static event Delegates.CompareTo_AfterArgs CompareTo
{
add
{

HookHandler._afterCompareTo += value;
HookHandler.HookCompareTo();
}
remove => HookHandler._afterCompareTo -= value;
}
}

/// <summary>
/// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
/// </summary>
public static class WithOrig
{
public static event Delegates.CopyFrom_WithArgs CopyFrom
{
add => HookEndpointManager.Add<Delegates.CopyFrom_WithArgs>(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteDefinition.AttachPoint), "CopyFrom", true), value);
remove => HookEndpointManager.Remove<Delegates.CopyFrom_WithArgs>(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteDefinition.AttachPoint), "CopyFrom", true), value);
}
public static event Delegates.CompareTo_WithArgs CompareTo
{
add => HookEndpointManager.Add<Delegates.CompareTo_WithArgs>(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteDefinition.AttachPoint), "CompareTo", true), value);
remove => HookEndpointManager.Remove<Delegates.CompareTo_WithArgs>(ReflectionHelper.GetMethodInfo(typeof(tk2dSpriteDefinition.AttachPoint), "CompareTo", true), value);
}
}

}
