using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for tk2dSystem class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class Ontk2dSystem
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hookget_inst()
        {
            if (!HookedList.Contains("get_inst"))
            {
                HookedList.Add("get_inst");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_inst", false), get_inst);
            }
        }

        internal static event Delegates.get_inst_BeforeArgs _beforeget_inst;
        internal static event Delegates.get_inst_AfterArgs _afterget_inst;

        private static tk2dSystem get_inst(Func<tk2dSystem> orig)
        {
            if (_beforeget_inst != null)
                foreach (Delegates.get_inst_BeforeArgs toInvoke in _beforeget_inst.GetInvocationList())
                    try
                    {
                        _beforeget_inst?.Invoke();
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            var retVal = orig();
            if (_afterget_inst != null)
                foreach (Delegates.get_inst_AfterArgs toInvoke in _afterget_inst.GetInvocationList())
                    try
                    {
                        retVal = _afterget_inst.Invoke(retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            return retVal;
        }

        internal static void Hookget_inst_NoCreate()
        {
            if (!HookedList.Contains("get_inst_NoCreate"))
            {
                HookedList.Add("get_inst_NoCreate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_inst_NoCreate", false),
                    get_inst_NoCreate);
            }
        }

        internal static event Delegates.get_inst_NoCreate_BeforeArgs _beforeget_inst_NoCreate;
        internal static event Delegates.get_inst_NoCreate_AfterArgs _afterget_inst_NoCreate;

        private static tk2dSystem get_inst_NoCreate(Func<tk2dSystem> orig)
        {
            if (_beforeget_inst_NoCreate != null)
                foreach (Delegates.get_inst_NoCreate_BeforeArgs toInvoke in
                         _beforeget_inst_NoCreate.GetInvocationList())
                    try
                    {
                        _beforeget_inst_NoCreate?.Invoke();
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            var retVal = orig();
            if (_afterget_inst_NoCreate != null)
                foreach (Delegates.get_inst_NoCreate_AfterArgs toInvoke in _afterget_inst_NoCreate.GetInvocationList())
                    try
                    {
                        retVal = _afterget_inst_NoCreate.Invoke(retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            return retVal;
        }

