        private static string get_CurrentPlatform(Func<string> orig)
        {
            if (_beforeget_CurrentPlatform != null)
                foreach (Delegates.get_CurrentPlatform_BeforeArgs toInvoke in _beforeget_CurrentPlatform
                             .GetInvocationList())
                    try
                    {
