                        HKMirrorMod.DoLogError(e);
                    }

            var retVal = orig();
            if (_afterget_CurrentPlatform != null)
                foreach (Delegates.get_CurrentPlatform_AfterArgs toInvoke in
                         _afterget_CurrentPlatform.GetInvocationList())
