        internal static void Hookset_CurrentPlatform()
        {
            if (!HookedList.Contains("set_CurrentPlatform"))
            {
                HookedList.Add("set_CurrentPlatform");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "set_CurrentPlatform", false),
                    set_CurrentPlatform);
            }
        }

        internal static event Delegates.set_CurrentPlatform_BeforeArgs _beforeset_CurrentPlatform;
        internal static event Delegates.set_CurrentPlatform_AfterArgs _afterset_CurrentPlatform;

        private static void set_CurrentPlatform(Action<string> orig, string value)
        {
            Delegates.Params_set_CurrentPlatform @params = new()
            {
                value = value
            };
            if (_beforeset_CurrentPlatform != null)
                foreach (Delegates.set_CurrentPlatform_BeforeArgs toInvoke in _beforeset_CurrentPlatform
                             .GetInvocationList())
                    try
                    {
                        _beforeset_CurrentPlatform?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            value = @params.value;
            orig(value);
            if (_afterset_CurrentPlatform != null)
                foreach (Delegates.set_CurrentPlatform_AfterArgs toInvoke in
                         _afterset_CurrentPlatform.GetInvocationList())
                    try
                    {
                        _afterset_CurrentPlatform.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

