    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_inst_WithArgs get_inst
        {
            add => HookEndpointManager.Add<Delegates.get_inst_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_inst", false), value);
            remove => HookEndpointManager.Remove<Delegates.get_inst_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_inst", false), value);
        }

        public static event Delegates.get_inst_NoCreate_WithArgs get_inst_NoCreate
        {
            add => HookEndpointManager.Add<Delegates.get_inst_NoCreate_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_inst_NoCreate", false), value);
            remove => HookEndpointManager.Remove<Delegates.get_inst_NoCreate_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_inst_NoCreate", false), value);
        }

        public static event Delegates.get_CurrentPlatform_WithArgs get_CurrentPlatform
        {
            add => HookEndpointManager.Add<Delegates.get_CurrentPlatform_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(tk2dSystem), "get_CurrentPlatform", false), value);
