        internal static event Delegates.CalculateNewPos_BeforeArgs _beforeCalculateNewPos;
        internal static event Delegates.CalculateNewPos_AfterArgs _afterCalculateNewPos;

        private static UnityEngine.Vector3 CalculateNewPos(On.tk2dUIDragItem.orig_CalculateNewPos orig,
            tk2dUIDragItem self)
        {
            Delegates.Params_CalculateNewPos @params = new()
            {
                self = self
            };
            if (_beforeCalculateNewPos != null)
            {
                foreach (Delegates.CalculateNewPos_BeforeArgs toInvoke in _beforeCalculateNewPos.GetInvocationList())
                {
                    try
                    {
                        _beforeCalculateNewPos?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterCalculateNewPos != null)
            {
                foreach (Delegates.CalculateNewPos_AfterArgs toInvoke in _afterCalculateNewPos.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterCalculateNewPos.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

