    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_OnEnable
        {
            public tk2dUIDragItem self;
        }

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_AfterArgs(Params_OnEnable args);

        public delegate void OnEnable_WithArgs(Action<tk2dUIDragItem> orig, tk2dUIDragItem self);

        public sealed class Params_OnDisable
        {
            public tk2dUIDragItem self;
        }

        public delegate void OnDisable_BeforeArgs(Params_OnDisable args);

        public delegate void OnDisable_AfterArgs(Params_OnDisable args);

        public delegate void OnDisable_WithArgs(Action<tk2dUIDragItem> orig, tk2dUIDragItem self);

        public sealed class Params_UpdateBtnPosition
        {
            public tk2dUIDragItem self;
        }

        public delegate void UpdateBtnPosition_BeforeArgs(Params_UpdateBtnPosition args);

        public delegate void UpdateBtnPosition_AfterArgs(Params_UpdateBtnPosition args);

        public delegate void UpdateBtnPosition_WithArgs(Action<tk2dUIDragItem> orig, tk2dUIDragItem self);

        public sealed class Params_CalculateNewPos
        {
            public tk2dUIDragItem self;
        }

        public delegate void CalculateNewPos_BeforeArgs(Params_CalculateNewPos args);

        public delegate UnityEngine.Vector3 CalculateNewPos_AfterArgs(Params_CalculateNewPos args,
            UnityEngine.Vector3 ret);

        public delegate UnityEngine.Vector3 CalculateNewPos_WithArgs(Func<tk2dUIDragItem, UnityEngine.Vector3> orig,
            tk2dUIDragItem self);

        public sealed class Params_ButtonDown
        {
            public tk2dUIDragItem self;
        }

        public delegate void ButtonDown_BeforeArgs(Params_ButtonDown args);

        public delegate void ButtonDown_AfterArgs(Params_ButtonDown args);

        public delegate void ButtonDown_WithArgs(Action<tk2dUIDragItem> orig, tk2dUIDragItem self);

        public sealed class Params_ButtonRelease
        {
            public tk2dUIDragItem self;
        }

        public delegate void ButtonRelease_BeforeArgs(Params_ButtonRelease args);

        public delegate void ButtonRelease_AfterArgs(Params_ButtonRelease args);

        public delegate void ButtonRelease_WithArgs(Action<tk2dUIDragItem> orig, tk2dUIDragItem self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.OnDisable_BeforeArgs OnDisable
        {
            add
            {
                HookHandler._beforeOnDisable += value;
