namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for tk2dUILayoutContainerSizer class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class Ontk2dUILayoutContainerSizer
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookDoChildLayout()
        {
            if (!HookedList.Contains("DoChildLayout"))
            {
                HookedList.Add("DoChildLayout");
                On.tk2dUILayoutContainerSizer.DoChildLayout += DoChildLayout;
            }
        }

        internal static event Delegates.DoChildLayout_BeforeArgs _beforeDoChildLayout;
        internal static event Delegates.DoChildLayout_AfterArgs _afterDoChildLayout;

        private static void DoChildLayout(On.tk2dUILayoutContainerSizer.orig_DoChildLayout orig,
            tk2dUILayoutContainerSizer self)
        {
            Delegates.Params_DoChildLayout @params = new()
            {
                self = self
            };
            if (_beforeDoChildLayout != null)
                foreach (Delegates.DoChildLayout_BeforeArgs toInvoke in _beforeDoChildLayout.GetInvocationList())
                    try
                    {
                        _beforeDoChildLayout?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterDoChildLayout != null)
                foreach (Delegates.DoChildLayout_AfterArgs toInvoke in _afterDoChildLayout.GetInvocationList())
                    try
                    {
                        _afterDoChildLayout.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoChildLayout_AfterArgs(Params_DoChildLayout args);

        public delegate void DoChildLayout_BeforeArgs(Params_DoChildLayout args);

        public delegate void DoChildLayout_WithArgs(Action<tk2dUILayoutContainerSizer> orig,
            tk2dUILayoutContainerSizer self);

        public sealed class Params_DoChildLayout
        {
            public tk2dUILayoutContainerSizer self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.DoChildLayout_BeforeArgs DoChildLayout
        {
            add
            {
                HookHandler._beforeDoChildLayout += value;
                HookHandler.HookDoChildLayout();
            }
            remove => HookHandler._beforeDoChildLayout -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.DoChildLayout_AfterArgs DoChildLayout
        {
            add
            {
                HookHandler._afterDoChildLayout += value;
                HookHandler.HookDoChildLayout();
            }
            remove => HookHandler._afterDoChildLayout -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.tk2dUILayoutContainerSizer.hook_DoChildLayout DoChildLayout
        {
            add => On.tk2dUILayoutContainerSizer.DoChildLayout += value;
            remove => On.tk2dUILayoutContainerSizer.DoChildLayout -= value;
        }
    }
}