        internal static event Delegates.QueueCommit_AfterArgs _afterQueueCommit;

        private static void QueueCommit(On.tk2dUpdateManager.orig_QueueCommit orig, tk2dTextMesh textMesh)
        {
            Delegates.Params_QueueCommit @params = new()
            {
                textMesh = textMesh
            };
            if (_beforeQueueCommit != null)
            {
                foreach (Delegates.QueueCommit_BeforeArgs toInvoke in _beforeQueueCommit.GetInvocationList())
                {
                    try
                    {
                        _beforeQueueCommit?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            textMesh = @params.textMesh;
            orig(textMesh);
            if (_afterQueueCommit != null)
            {
                foreach (Delegates.QueueCommit_AfterArgs toInvoke in _afterQueueCommit.GetInvocationList())
                {
                    try
                    {
                        _afterQueueCommit.Invoke(@params);
