    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void get_Instance_BeforeArgs();

        public delegate tk2dUpdateManager get_Instance_AfterArgs(tk2dUpdateManager ret);

        public delegate tk2dUpdateManager get_Instance_WithArgs(Func<tk2dUpdateManager> orig);

        public sealed class Params_QueueCommit
        {
            public tk2dTextMesh textMesh;
        }

        public delegate void QueueCommit_BeforeArgs(Params_QueueCommit args);

        public delegate void QueueCommit_AfterArgs(Params_QueueCommit args);

        public delegate void QueueCommit_WithArgs(Action<tk2dTextMesh> orig, tk2dTextMesh textMesh);

        public delegate void FlushQueues_BeforeArgs();

        public delegate void FlushQueues_AfterArgs();

        public delegate void FlushQueues_WithArgs(Action orig);

        public sealed class Params_OnEnable
        {
            public tk2dUpdateManager self;
        }

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_AfterArgs(Params_OnEnable args);

        public delegate void OnEnable_WithArgs(Action<tk2dUpdateManager> orig, tk2dUpdateManager self);

        public sealed class Params_LateUpdate
        {
            public tk2dUpdateManager self;
        }

        public delegate void LateUpdate_BeforeArgs(Params_LateUpdate args);

        public delegate void LateUpdate_AfterArgs(Params_LateUpdate args);

        public delegate void LateUpdate_WithArgs(Action<tk2dUpdateManager> orig, tk2dUpdateManager self);

        public sealed class Params_coSuperLateUpdate
        {
            public tk2dUpdateManager self;
        }

        public delegate void coSuperLateUpdate_BeforeArgs(Params_coSuperLateUpdate args);

        public delegate System.Collections.IEnumerator coSuperLateUpdate_AfterArgs(Params_coSuperLateUpdate args,
            System.Collections.IEnumerator ret);

        public delegate System.Collections.IEnumerator coSuperLateUpdate_WithArgs(
            Func<tk2dUpdateManager, System.Collections.IEnumerator> orig, tk2dUpdateManager self);

        public sealed class Params_QueueCommitInternal
        {
            public tk2dUpdateManager self;
            public tk2dTextMesh textMesh;
        }

        public delegate void QueueCommitInternal_BeforeArgs(Params_QueueCommitInternal args);

        public delegate void QueueCommitInternal_AfterArgs(Params_QueueCommitInternal args);

        public delegate void QueueCommitInternal_WithArgs(Action<tk2dUpdateManager, tk2dTextMesh> orig,
            tk2dUpdateManager self, tk2dTextMesh textMesh);

        public sealed class Params_FlushQueuesInternal
        {
            public tk2dUpdateManager self;
        }

        public delegate void FlushQueuesInternal_BeforeArgs(Params_FlushQueuesInternal args);

        public delegate void FlushQueuesInternal_AfterArgs(Params_FlushQueuesInternal args);

        public delegate void FlushQueuesInternal_WithArgs(Action<tk2dUpdateManager> orig, tk2dUpdateManager self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_Instance_BeforeArgs get_Instance
        {
            add
            {
                HookHandler._beforeget_Instance += value;
                HookHandler.Hookget_Instance();
            }
            remove => HookHandler._beforeget_Instance -= value;
        }

